import React from 'react';
import {Container, Nav, Navbar} from "react-bootstrap";
import {NavLink} from "react-router-dom";

const Header = () => {
    const {setUser, setIsAuth} = useContext(AuthContext);
    const navigate = useNavigate();
    
    const logout = () => {
        localStorage.removeItem('token');
        setUser({});
        setIsAuth(false);
        navigate(LOGIN_ROUTE);
    }

    return (
        <Navbar className="mb-4" bg="primary" expand="lg">
            <Container>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav>
                        <NavLink style={{color: "white"}} className="nav-link fs-5 me-5">Бронировать</NavLink>
                        <NavLink style={{color: "white"}} className="nav-link fs-5 me-5">Мои бронирования</NavLink>
                        <NavLink style={{color: "white"}} className="nav-link fs-5 me-5">История бронирований</NavLink>
                        <NavLink style={{color: "white"}} className="nav-link fs-5 me-5">Выйти</NavLink>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default Header;
