import React from 'react';
import {CloseButton} from "react-bootstrap";
import {deleteTable} from "../http/tableApi";
import tables from "../pages/Tables";

const Table = ({id, name, capacity, setTables}) => {

    const delTable = async () => {
        try {
            await deleteTable(id);
            setTables(tables =>  tables.filter(table => table.id != id));
        }catch (e) {
            console.log(e);
        }
    }

    return (
        <div
            style={{background: "#f2f2f2", borderRadius: "10px", height: "70px"}}
            className="position-relative d-flex flex-column justify-content-center">
                <p className="mb-0">{name}</p>
                <p className="mb-0">Вместимость: {capacity}</p>
                <CloseButton
                    className="position-absolute top-0 end-0"
                    onClick={delTable}
                />
        </div>
    );
};

export default Table;
