import React, {useState} from 'react';
import {Button, Card, Container, Form} from "react-bootstrap";
import {ROLES} from "../utils/roles";
import {registration} from "../http/userApi";
import * as Yup from 'yup';
import {Formik} from "formik";

const Registration = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [role, setRole] = useState(ROLES.USER);

    const validationSchema = Yup.object().shape({
        email: Yup.string().email('Некорректный адрес электронной почты').required('Обязательное поле'),
        password: Yup.string().min(6, 'Пароль должен содержать не менее 6 символов').required('Обязательное поле'),
    });


    const registerUser = async () => {
        try {
            const response = await registration(username, password, role);
            console.log(response)
            // setUser({
            //     id: response.id,
            //     email: response.email,
            // });
            // setIsAuth(true);
            // navigate(MAIN_ROUTE);
        }catch (e) {
            // const possibleErrors = e.response.data.errors;
            // if(possibleErrors.length !== 0) {
            //     const err = {};
            //     possibleErrors.forEach(error => err[error.path] = error.msg);
            //     // setErrors(err);
            // }
        }
    }

    return (Formik)

    return (
        <Container
            className="d-flex justify-content-center align-items-center"
            style={{height: window.innerHeight - 60}}
        >
            <Card style={{width: 450}} className="p-4 border-1">
                <h2 className="m-auto mb-4">Регистрация</h2>

                <Form className="d-flex flex-column">
                    <Form.Group className="d-flex justify-content-center mb-4" controlId="kindOfStand">
                        <Form.Check
                            type="radio"
                            value={ROLES.USER}
                            aria-label="radio 1"
                            label="Пользователь"
                            onChange={e => setRole(e.target.value)}
                            className="pe-4"
                            checked={role === ROLES.USER}
                        />

                        <Form.Check
                            type="radio"
                            value={ROLES.ADMIN}
                            aria-label="radio 2"
                            label="Администратор"
                            onChange={e => setRole(e.target.value)}
                            checked={role === ROLES.ADMIN}
                        />
                    </Form.Group>

                    <Form.Group className="mb-5">
                        <Form.Control
                            className="mb-3"
                            placeholder="Введите имя пользователя"
                            value={username}
                            onChange={e => setUsername(e.target.value)}
                        />

                        <Form.Control
                            type="password"
                            placeholder="Введите пароль"
                            value={password}
                            onChange={e => setPassword(e.target.value)}
                        />
                    </Form.Group>

                    <Button onClick={registerUser}>Зарегистрироваться</Button>
                </Form>
            </Card>
        </Container>
    );
};

export default Registration;
