import React, {useContext, useEffect, useState} from 'react';
import {AuthContext} from "../contexts";
import {getBooking} from "../http/Booking";
import Header from "../components/Header";
import {Container} from "react-bootstrap";

const MyBooking = () => {
    const {user} = useContext(AuthContext);
    const [booking, setBooking] = useState([]);

    useEffect(() => {
        getBooking(user.id)
            .then((bookings) => setBooking(bookings));
    }, []);

    console.log(booking)
    return (
        <div>
            <Header/>
            <Container>
                {
                    booking.length === 0 ? (
                        <p>У вас нет ни одного бронирования.</p>
                    ) : (
                        <ul>
                            {booking.map((booking) => (
                                <li key={booking.id}>
                                    <span>Столик №{booking.tableNumber}</span>
                                    <span>Дата: {booking.bookingDate}</span>
                                    <span>Время: {booking.startTime} - {booking.endTime}</span>
                                    <button onClick={() => handleDeleteBooking(booking.id)}>Удалить</button>
                                </li>
                            ))}
                        </ul>
                    )
                }
            </Container>
        </div>
    );
};

export default MyBooking;
