import React, {useState} from 'react';
import {Button, Card, Container, Form} from "react-bootstrap";
import {ROLES} from "../utils/roles";
import {registration} from "../http/userApi";
import * as Yup from 'yup';
import {Formik} from "formik";
import {useNavigate} from "react-router-dom";

const Registration = () => {
    const [role, setRole] = useState(ROLES.USER);
    const navigate = useNavigate();

    const validationSchema = Yup.object().shape({
        username: Yup.string().required('Обязательное поле'),
        password: Yup.string().min(6, 'Пароль должен содержать не менее 6 символов').required('Обязательное поле'),
    });


    const registerUser = async (values) => {
        try {
            const {username, password} = values;
            const {id, role} = await registration(username, password, role);
            
            console.log(response)
        } catch (e) {
            console.log(e);
        }
    };


    return (
        <Container
            className="d-flex justify-content-center align-items-center"
            style={{height: window.innerHeight - 60}}
        >
            <Card style={{width: 400}} className="p-4 border-1">
                <Formik
                    initialValues={{username: '', password: ''}}
                    validationSchema={validationSchema}
                    onSubmit={registerUser}>
                    {({
                          values,
                          errors,
                          touched,
                          handleChange,
                          handleBlur,
                          handleSubmit,
                          isValidating,
                      }) => (
                        <Form onSubmit={handleSubmit} className="d-flex flex-column justify-content-center">
                            <h2 className="m-auto mb-4">Регистрация</h2>
                            <Form.Group className="d-flex justify-content-center mb-4">
                                <Form.Check
                                    type="radio"
                                    label="Пользователь"
                                    value={ROLES.USER}
                                    onChange={e => setRole(e.target.value)}
                                    className="pe-4"
                                    checked={role === ROLES.USER}
                                />

                                <Form.Check
                                    type="radio"
                                    label="Администратор"
                                    value={ROLES.ADMIN}
                                    onChange={e => setRole(e.target.value)}
                                    checked={role === ROLES.ADMIN}
                                />
                            </Form.Group>

                            <Form.Group className="mb-3">
                                <Form.Control
                                    name="username"
                                    placeholder="Введите имя пользователя"
                                    value={values.username}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    isInvalid={touched.username && errors.username}
                                />

                                <Form.Control.Feedback type="invalid">{errors.username}</Form.Control.Feedback>
                            </Form.Group>

                            <Form.Group className="mb-4">
                                <Form.Control
                                    type="password"
                                    placeholder="Введите пароль"
                                    name="password"
                                    value={values.password}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    isInvalid={touched.password && errors.password}
                                />

                                <Form.Control.Feedback type="invalid">{errors.password}</Form.Control.Feedback>
                            </Form.Group>

                            <Button
                                type="submit"
                                disabled={isValidating}
                                style={{width: "70%"}}
                                className="m-auto"
                            >
                                Зарегистрироваться
                            </Button>
                        </Form>
                    )}
                </Formik>
            </Card>
        </Container>
    );
};

export default Registration;
