import React, {useState} from 'react';
import {Button, Card, Container, Form} from "react-bootstrap";
import {ROLES} from "../utils/roles";
import {registration} from "../http/userApi";
import * as Yup from 'yup';
import {Formik} from "formik";

const Registration = () => {
    // const [username, setUsername] = useState("");
    // const [password, setPassword] = useState("");
    const [role, setRole] = useState(ROLES.USER);

    const validationSchema = Yup.object().shape({
        username: Yup.string().email('Некорректный адрес электронной почты').required('Обязательное поле'),
        password: Yup.string().min(6, 'Пароль должен содержать не менее 6 символов').required('Обязательное поле'),
    });


    const registerUser = async () => {
        try {
            const response = await registration(username, password, role);
            console.log(response)
            // setUser({
            //     id: response.id,
            //     email: response.email,
            // });
            // setIsAuth(true);
            // navigate(MAIN_ROUTE);
        }catch (e) {
            // const possibleErrors = e.response.data.errors;
            // if(possibleErrors.length !== 0) {
            //     const err = {};
            //     possibleErrors.forEach(error => err[error.path] = error.msg);
            //     // setErrors(err);
            // }
        }
    }

    const handleSubmit = (values, { setSubmitting }) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
        }, 1000);
    };


    return (
        <Container
            className="d-flex justify-content-center align-items-center"
            style={{height: window.innerHeight - 60}}
        >
            <Card style={{width: 450}} className="p-4 border-1">
                <Formik
                    initialValues={{username: '', password: ''}}
                    onSubmit={handleSubmit}>
                    {({
                          values,
                          errors,
                          touched,
                          handleChange,
                          handleBlur,
                          handleSubmit,
                          isSubmitting,
                      }) => (
                    <Form onSubmit={handleSubmit} className="d-flex flex-column">
                        <h2 className="m-auto mb-4">Регистрация</h2>
                        <Form.Group className="d-flex justify-content-center mb-4" controlId="kindOfStand">
                            <Form.Check
                                type="radio"
                                value={ROLES.USER}
                                aria-label="radio 1"
                                label="Пользователь"
                                onChange={e => setRole(e.target.value)}
                                className="pe-4"
                                checked={role === ROLES.USER}
                            />

                            <Form.Check
                                type="radio"
                                value={ROLES.ADMIN}
                                aria-label="radio 2"
                                label="Администратор"
                                onChange={e => setRole(e.target.value)}
                                checked={role === ROLES.ADMIN}
                            />
                        </Form.Group>

                        <Form.Group className="mb-5">
                            <Form.Control
                                className="mb-3"
                                placeholder="Введите имя пользователя"
                                name="username"
                                value={values.username}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                isInvalid={touched.email && errors.email}
                            />

                            <Form.Control.Feedback type="invalid">
                                {errors.email}
                            </Form.Control.Feedback>

                            <Form.Control
                                type="password"
                                placeholder="Введите пароль"
                                name="password"
                                value={values.password}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                isInvalid={touched.password && errors.password}
                            />

                            <Form.Control.Feedback type="invalid">
                                {errors.password}
                            </Form.Control.Feedback>
                        </Form.Group>

                        <Button onClick={registerUser}>Зарегистрироваться</Button>
                    </Form>
                    )}
                </Formik>
            </Card>
        </Container>
    );
};

export default Registration;
