import React, {useState} from 'react';
import {Button, Card, Container, Form} from "react-bootstrap";
import {ROLES} from "../utils/roles";

const Registration = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [role, setRole] = useState({
        user: ROLES.USER,
        admin: "",
    });

    const handleRole = (e) => {
        if(e.target.value === ROLES.USER) {
            setRole({user: ROLES.USER, admin: ""});
        }else {
            setRole({user: "", admin: ROLES.ADMIN});
        }
    }

    return (
        <Container
            className="d-flex justify-content-center align-items-center"
            style={{height: window.innerHeight - 60}}
        >
            <Card style={{width: 450}} className="p-4 border-1">
                <h2 className="m-auto mb-4">Регистрация</h2>
                <Form className="d-flex flex-column">
                    <Form.Group className="d-flex justify-content-center mb-4" controlId="kindOfStand">
                        <Form.Check
                            type="radio"
                            value={ROLES.USER}
                            aria-label="radio 1"
                            label="Пользователь"
                            onChange={handleRole}
                            className="pe-4"
                            checked={role.user === ROLES.USER}
                        />

                        <Form.Check
                            type="radio"
                            value={ROLES.ADMIN}
                            aria-label="radio 2"
                            label="Администратор"
                            onChange={handleRole}
                            checked={role.admin === ROLES.ADMIN}
                        />
                    </Form.Group>

                    <Form.Group className="mb-5">
                        <Form.Control
                            className="mb-3"
                            placeholder="Введите имя пользователя"
                            value={username}
                            onChange={e => setUsername(e.target.value)}
                        />

                        <Form.Control
                            type="password"
                            placeholder="Введите пароль"
                            value={password}
                            onChange={e => setPassword(e.target.value)}
                        />
                    </Form.Group>

                    <Button>Зарегистрироваться</Button>
                </Form>
            </Card>
        </Container>
    );
};

export default Registration;
